            item.classList.remove('shake');
          },
          { once: true },
        );
      } else {
        item.classList.add('opacity-50');
        dropItem.classList.remove('text-white');
        dropItem.classList.add('text-stone-700');
      }
    }

    //...
  },
  { once: true },
);
